#!/usr/bin/env bash

test() {
	echo -e "\e[1mCONVERT ${1}:\e[0m"
	./convert "${1}"
}

test '123g'
test '0'
test '0f'
test '0f.'
test '.f'
test '.0000f'
test '0.f'
test '0.0ff'
test '.'
test '.1'
test '0.1.'
test '-10'
test '42'
test '*'
test '~'
test '42.0'
test '42.0f'
test '-42.0'
test '-42.0f'
test '42.0ff'
test '42.0ff09'
test '42.238476'
test '42.238476f'

test 'nan'
test 'nanf'
test '+inf'
test '+inff'
test '-inf'
test '-inff'

test '127'
test '128'
test '2147483647' #INT_MAX
test '2147483648' #INT overflow
test '2147483648.0f' #INT overflow
test '2147483648.0' #INT overflow
test '-2147483647' #INT_MIN
test '-2147483648' #INT_MIN
test '9223372036854775807' #LLONG_MAX
test '-9223372036854775807' #LLONG_MIN
test '92233720368547758070' #LLONG_MAX * 10
test '340282346638528859811704183484516925440.0f' #FLOAT MAX
test '3402823466385288598117041834845169254400.0f' #FLOAT overflow
test '3402823466385288598117041834845169254400.0' #FLOAT overflow
test '.0' #DOUBLE MAX
